pragma solidity >=0.4.22 <0.9.0;

/**
 * @title Escrow contract
 * @dev main interface of Escrow smart contract
 */

import "./ownable.sol";

interface Perseus {
    function balanceOf(address account) external view returns (uint256);
}

 contract Escrow is Ownable {
     address tokenAddress = 0x65dc57A9e458211774c82cfFeB70f83939742A59;
     Perseus perseusContract = Perseus(tokenAddress);

     /// @notice struct of escrow order
     struct Order {
        address buyer;
        address seller;
        uint8 feeMode;
        uint256 price;
        uint160 orderId;
     }

     /// @notice array of escrow orders
     Order[] orders;

    /**
      * @dev check if msg.sender is the buyer registered in the orders.
      * @param _orderId id that saved in the escrow database
      */
     modifier isBuyer(uint _orderId) {
        uint isbuyer = 0;
        for (uint i = 0; i < orders.length; i++) {
            if (orders[i].orderId == _orderId && orders[i].buyer == msg.sender) {
                isbuyer = 1;
            }
        }
        if (msg.sender == owner()) {
            isbuyer = 1;
        }
        require(isbuyer == 1);
        _;
     }

    /**
      * @dev check if msg.sender is the seller registered in the orders.
      * @param _orderId id that saved in the escrow database
      */
     modifier isSeller(uint _orderId) {
        uint isseller = 0;
        for (uint i = 0; i < orders.length; i++) {
            if (orders[i].orderId == _orderId && orders[i].seller == msg.sender) {
                isseller = 1;
            }
        }
        if (msg.sender == owner()) {
            isseller = 1;
        }
        require(isseller == 1);
        _;
     }

     /**
      * @dev register new Order to smart contract with infomations of buyer, seller, feeMode, and price.
      * @param _buyerAddress address of buyer
      * @param _sellerAddress address of seller
      * @param _feeMode the variable who pay the escrow fee, 0 -> seller, 1 -> both
      * @param _price the fund amount of transaction
      * @param _orderId id that saved in the escrow database
      */
     function createOrder(address _buyerAddress, address _sellerAddress, uint _feeMode, uint _price, uint _orderId) external payable {
         uint price = _price;
        //  if (_feeMode == 1 && perseusContract.balanceOf(msg.sender) == 0) {
        //     price = price + _price / 2000;
        //  }
         if (_feeMode == 1) {
            price = price + _price / 2000;
         }
         require(msg.value == price, "failed to receive the bnb");
         orders.push(Order(_buyerAddress, _sellerAddress, uint8(_feeMode), _price, uint160(_orderId)));
     }  

    /**
      * @dev check if msg.sender has perseus token.
      */
     function hasPerseusToken() external view returns(uint) {
         if (perseusContract.balanceOf(msg.sender) == 0) {
            return 0;
         } else {
             return 1;
         }
     }

    /**
      * @dev withdraw the bnb from smart contract by the buyer.
      */
     function release(uint _orderId) external isBuyer(_orderId) {
        uint result = 0;
        for (uint i = 0; i < orders.length; i++) {
            if (orders[i].orderId == _orderId) {
                uint price = orders[i].price - orders[i].price / 1000;
                // if (perseusContract.balanceOf(orders[i].seller) != 0) {
                //     price = orders[i].price;
                // } else if (orders[i].feeMode == 1) {
                //     price = orders[i].price - orders[i].price / 2000;
                // }
                if (orders[i].feeMode == 1) {
                    price = orders[i].price - orders[i].price / 2000;
                }
                address payable _payAdr = address(uint160(orders[i].seller));
                _payAdr.transfer(price);
                result = 1;
            }
        }
        require(result == 1, "There is no matching orderId");
     }

    /**
      * @dev withdraw the bnb from smart contract by the seller.
      */
     function refund(uint _orderId) external isSeller(_orderId) {
        uint result = 0;
        for (uint i = 0; i < orders.length; i++) {
            if (orders[i].orderId == _orderId) {
                address payable _payAdr = address(uint160(orders[i].buyer));
                _payAdr.transfer(orders[i].price);
            }
        }
        require(result == 1, "There is no matching orderId");
     }

    /**
      * @dev withdraw the bnb from smart contract by the seller.
      */
     function withdraw() external onlyOwner {
        address payable _owner = address(uint160(owner()));
        _owner.transfer(address(this).balance);
     }

 }

